    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        /*
        Eliminar una transaccion e informa si se elimino o no.
        Llama a la funci√≥n para eliminar transacciones presente en la logica del programa.
        */
        boolean delete = logica.delete_Transaccion(transaccion.getId());
        if (delete){
             JOptionPane.showMessageDialog(this, "Transaccion eliminada correctamente");
             this.setVisible(false);
             this.dispose();
        }
        else{
             JOptionPane.showMessageDialog(this, "La transaccion especificada no pudo ser eliminada.");
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

